(213, 1, '2020-09-17 21:54:49', '2020-09-17 21:54:49', '<p>[vc_row full_width=\"stretch_row_content_no_spaces\" css=\".vc_custom_1588731907540{margin-top: 0px !important;padding-top: 0px !important;background-color: #00b400 !important;}\"][vc_column css=\".vc_custom_1594131758969{padding-top: 0px !important;}\" offset=\"vc_hidden-xs\"][rev_slider alias=\"portada\"][/vc_column][/vc_row][vc_row full_width=\"stretch_row\" css=\".vc_custom_1588194806232{margin-top: 0px !important;padding-top: 0px !important;background-color: #00b400 !important;}\"][vc_column css=\".vc_custom_1594131766927{padding-top: 0px !important;}\" offset=\"vc_hidden-lg vc_hidden-md vc_hidden-sm\"][rev_slider alias=\"portada-movil\"][/vc_column][/vc_row][vc_row full_width=\"stretch_row\" css=\".vc_custom_1593537826032{padding-top: 0px !important;background-color: #00b400 !important;}\"][vc_column width=\"1/6\" css=\".vc_custom_1593273610589{padding-top: 0px !important;}\"][/vc_column][vc_column css_animation=\"none\" width=\"2/3\" css=\".vc_custom_1593537834257{padding-top: 32px !important;}\" offset=\"vc_hidden-sm vc_hidden-xs\"][vc_custom_heading text=\"<strong>TECNOLOGÍA EUROPEA<br />\r\nAHORA EN PERÚ</strong>\" font_container=\"tag:h2|font_size:32|text_align:center|color:%23ffffff|line_height:1\" use_theme_fonts=\"yes\" css_animation=\"fadeInUp\"][vc_single_image image=\"47\" img_size=\"full\" alignment=\"center\" css_animation=\"fadeInUp\" css=\".vc_custom_1588110532892{margin-bottom: 0px !important;padding-bottom: 0px !important;}\"][vc_btn title=\"Ver más\" style=\"custom\" custom_background=\"#e21c21\" custom_text=\"#ffffff\" shape=\"square\" size=\"lg\" align=\"center\" css_animation=\"fadeInUp\" custom_onclick=\"true\" css=\".vc_custom_1600378347818{margin-top: 15px !important;}\" link=\"url:http%3A%2F%2Fwww.osteoporosisperu.com.pe%2Fnuestra-tecnologia%2F|||\"][vc_empty_space height=\"35px\"][vc_custom_heading text=\"Somos el Centro Médico más grande del país ESPECIALIZADO EN EL TRATAMIEN